import React, { Component } from "react";
import axios from "axios";
import Signin from "./SignIn";
import SignUp from "./SignUp";

class Auth extends Component {
  auth = (email, password, isSignup) => {
    this.setState({ email: email, password: password });
    console.log("auth", email);
    console.log("auth 2", password);

    const authData = {
      email: email,
      password: password,
    };

    let url = "";

    if (isSignup) {
      url = "http://localhost:3001/api/users?action=signup";
    } else {
      url = "http://localhost:3001/api/users?action=signin";
    }

    axios
      .post(url, authData)
      .then((response) => {
        console.log(response.data);
        if (!isSignup) {
          localStorage.setItem("token", response.data);
          this.props.history.push("/signinsuccess");
        } else {
          this.props.history.push("/signupsuccess");
        }
      })
      .catch((err) => {
        console.log("error status ", err.response.status);
        console.log("error message ", err.response.data);
      });
  };

  state = {
    url: "",
    email: "",
    password: "",
  };

  componentWillMount() {
    this.setState({ url: window.location.pathname });
  }

  render() {
    let signpage = "";

    if (this.state.url === "/signin") {
      console.log("invoked from Auth js");
      signpage = <Signin auth={this.auth}></Signin>;
    } else {
      console.log("invoked from Auth js");
      signpage = <SignUp auth={this.auth}></SignUp>;
    }

    return <div>{signpage}</div>;
  }
}

export default Auth;
