import React, { Component } from "react";
import axios from "axios";

class Welcome extends Component {
  state = {
    welcomeMessage: '',
  };

  fetchMessage = () => {
    axios
      .get("http://localhost:3001/api/welcome")
      .then((res) => {
        this.setState({ welcomeMessage: res.data });
      })
      .catch((ex) => {
        console.log("ex is ", ex);
      });
  };

  componentDidMount() {
    this.fetchMessage();
    setInterval(() => {
      this.fetchMessage();
    }, 2000);
  }

  /*
  componentWillUnmount() {
    clearInterval(this.interval);
  }
  */

  render() {
    return <div>{this.state.welcomeMessage}</div>;
  }
}

export default Welcome;
