import React, {useEffect} from 'react';
import axios from 'axios';


const Welcome = (props) => {

    let welcomeMessage = ''

    useEffect(() => {


        axios.get('http://localhost:3001/welcome')
    .then( res => {
        welcomeMessage = res.data;
    })
    .catch(ex =>    {
        welcomeMessage = 'Welcome to Financail Planner with ' + ex;
    })

       setInterval(() => {
        axios.get('http://localhost:3001/welcome')
        .then( res => {
            welcomeMessage = res.data;
        })
        .catch(ex =>    {
            welcomeMessage = 'Welcome to Financail Planner with ' + ex;
        })
          }, 10000)
    });





    return  (
        <div>

            {welcomeMessage}

        </div>

    );
};

export default Welcome;
