import React, { Component } from "react";
import axios from "axios";

class Welcome extends Component {
  state = {
    welcomeMessage: "",
  };

  fetchMessage = () => {

    axios
      .get("http://localhost:3001/welcome")
      .then((res) => {
        this.setState({ welcomeMessage: res.data });
      })
      .catch((ex) => {
        console.log('ex is ', ex);
      });

  };

  componentDidMount() {
    console.log("componentDidMount 1 ", this.state.welcomeMessage);

    this.setState({ welcomeMessage: this.fetchMessage() });

    console.log("componentDidMount 2 ", this.state.welcomeMessage);

    setInterval(() => {
      this.setState({ welcomeMessage: this.fetchMessage() });
    }, 10000);
  }

  render() {
    return <div>{this.state.welcomeMessage}</div>;
  }
}

export default Welcome;
