import React, { Component } from "react";
import axios from "axios";

class Welcome extends Component {
  state = {
    welcomeMessage: "",
  };

  fetchMessage = () => {
    let message = "";

    axios
      .get("http://localhost:3001/welcome")
      .then((res) => {
        message = res.data;
      })
      .catch((ex) => {
        console.log("ex is ", ex);
      });

    return message;
  };

  componentDidMount() {
    setInterval(() => {
      this.setState({ welcomeMessage: this.fetchMessage() });
    }, 10000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    return <div>{this.state.welcomeMessage}</div>;
  }
}

export default Welcome;
