import React, {useEffect} from 'react';
import axios from 'axios';

fetchInitData = ()  => {

    let welcomeMessage = ''

    axios.get('http://localhost:3001/welcome')
    .then( res => {
        welcomeMessage = res.data;
    })
    .catch(ex =>    {
        welcomeMessage = 'Welcome to Financail Planner with ' + ex;
    })

    return welcomeMessage;
}

const welcome = (props) =>  {

    let welcomeMessage = ''

    useEffect(() => {
        welcomeMessage =  fetchInitData();

        interval = setInterval(() => {
