import React, { Component } from "react";
import axios from "axios";
import Signin from "./SignIn";

const auth = (email, password, isSignup) => {
  const authData = {
    email: email,
    password: password,
  };

  let url = "";

  if (isSignup) {
    url = "http://localhost:3001/api/users?action=signup";
  } else {
    url = "http://localhost:3001/api/users?action=signin";
  }

  axios
    .post(url, authData)
    .then((response) => {
      console.log(response);
      console.log(console.data);
    })
    .catch((err) => {
      console.log("error signinup ", err);
    });
};

class Auth extends Component {
  state = {
    url: "",
  };

  componentWillMount() {
    console.log("pathname", window.location.pathname);
    console.log("href", window.location.href);
  }

  render() {
    return (
      <div>
        <Signin></Signin>
      </div>
    );
  }
}

export default Auth;
