import React, {useEffect} from 'react';
import axios from 'axios';

const welcome = (props) =>  {

    let welcomeMessage = ''

    useEffect(() => {
        fetchInitData();

        interval = setInterval(() => {
            this.fetchData()
          }, 10000)
    });


    fetchInitData = ()  => {
        axios.get('http://localhost:3001/welcome')
        .then( res => {
            welcomeMessage = res.data;
        })
        .catch(ex =>    {
            welcomeMessage = 'Welcome to Financail Planner with ' + ex;
        })

        return welcomeMessage;
    }

    return  (
        <div>

            {welcomeMessage}

        </div>

    );
};

export default welcome;
