import React, { Component } from "react";
import axios from "axios";
import Signin from "./SignIn";
import SignUp from "./SignUp";
import Snackbar from "@material-ui/core/Snackbar";

const AUTH_URL = "http://localhost:3001/api/users";

class Auth extends Component {
  auth = (fname, lname, email, password, isSignup) => {
    if (isSignup) {
      this.signUp(fname, lname, email, password);
    } else {
      this.signIn(email, password);
    }
  };

  signIn = (email, password) => {
    const authData = {
      email: email,
      password: password,
    };

    axios
      .post(AUTH_URL + "?action=signin", authData)
      .then((response) => {
        console.log(response.data);
        this.setState({ open: false, message: "" });
        localStorage.setItem("token", response.data);
        this.props.history.push("/signinsuccess");
      })
      .catch((err) => {
        console.log("error status ", err.message);
        console.error(err.response.data); // ***
        console.error(err.response.status); // ***
        console.error(err.response.headers); // ***
        this.setState({ open: true, message: err.message });
      });
  };

  signUp = (fname, lname, email, password) => {
    const authData = {
      fname: fname,
      lname: lname,
      email: email,
      password: password,
    };

    axios
      .post(AUTH_URL + "?action=signup", authData)
      .then((response) => {
        console.log(response.data);

        this.props.history.push("/signupsuccess");
      })
      .catch((err) => {
        console.log("error status ", err.response.status);
        console.log("error message ", err.response.data);
      });
  };

  state = {
    url: "",
    email: "",
    password: "",
    open: false,
    message: "",
  };

  componentWillMount() {
    this.setState({ url: window.location.pathname });
  }

  render() {
    let signpage = "";

    if (this.state.url === "/signin") {
      signpage = <Signin auth={this.auth}></Signin>;
    } else {
      signpage = <SignUp auth={this.auth}></SignUp>;
    }
    return (
      <div>
        {signpage}
        <Snackbar
          open={this.state.open}
          anchorOrigin={{ vertical: "top", horizontal: "right" }}
          message={this.state.message}
          autoHideDuration={6000}
        ></Snackbar>
      </div>
    );
  }
}

export default Auth;
